import styled  from 'styled-components';

export const Step = styled.div`  * {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  background: linear-gradient(180deg, #003D6E 0%, rgba(0, 42, 78, 0) 100%), #0F303E;
  color: ${props => props.theme.colors.text};
}


`;
