import React from 'react';
import ReactDOM from 'react-dom';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import EmailIcon from '@material-ui/icons/Email';
import Send from '@material-ui/icons/Send';


import { Container } from './style';

import img_etapa_01 from '../../../../assets/solicitacaoCredito/etapa1.png'


const useStyles = makeStyles(theme => ({
  root: {
    padding: theme.spacing(4),
    backgroundColor: theme.palette.common.black
  },
  input: {
    color: theme.palette.common.white,

    "&:hover $notchedOutline": {
      borderColor: theme.palette.grey[400]
    },
    "&$focused $notchedOutline": {
      borderColor: theme.palette.grey[400]
    },
  },
  notchedOutline: {
    borderColor: theme.palette.grey[400]
  },
  focused: {},
  adornedStart: {
    color: theme.palette.grey[400]
  }
}));

const Etapa01: React.FC = () => {
  const classes = useStyles();

  return (

    <Container className="h-full">

      <div className="md:flex h-full ">
        <div className="content-end md:flex-shrink-0 md:w-2/4">
          <img className="items-end etapa w-full" src={img_etapa_01} />
        </div>
        <div className="m-6 h-full">
          <div className="bem-vindo">Bem-vindx Ã  TMB</div>
          <div className="label-email">Informe seu e-mail</div>
          <div className="email">
            <Input id="txtEmail"
                classes={{
                  root: classes.input,
                  focused: classes.focused
                }}
              startAdornment={
                <InputAdornment position="start">
                  <EmailIcon />
                </InputAdornment>
              }
            />

          </div>
          <div className="criarConta flex justify-end">
            <Button
              variant="contained"
              className ="botao"
            >
              Criar sua conta
              <Send className="ml-4" />
            </Button>

          </div>
        </div>
      </div>
    </Container>


  );
}

export default Etapa01;
