// /pages/api/movies.js
import { PrismaClient } from '@prisma/client';
import {FichaBUS} from '../../src/business/fichaBUS'
const prisma = new PrismaClient();

export default async function (req, res) {

  const fichaBUS =  new FichaBUS();
  if (req.method === 'POST') {
    const { body } = req;
    const movie = await prisma.ficha.create({ data: JSON.parse(body) });
    res.json(movie);
  }
  if (req.method === 'GET') {
    const movie1 = await fichaBUS.create(null);
    const movie = await prisma.ficha.findMany();
    res.json(movie);
  }
}
