import React, { useCallback, useContext, useState } from 'react';
import Etapa01 from './Etapa01';
import Etapa02 from './Etapa02';
import { Container } from './style';
import { Ficha } from '../../../Entities/Ficha';
import EtapaContext, { iEtapa } from './etapaContext';
import { SentimentSatisfied } from '@material-ui/icons';
import { createMuiTheme, MuiThemeProvider } from '@material-ui/core/styles';

const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#992c2c'
    },
    secondary: {
      main: '#ffffff',
    },
    error: {
      light: '#e5a0a0',
      main: '#CD5C5C',
      dark: '#992c2c',
    },
    text: {
      primary:'#ffffff',
      secondary: '#ffffff',
      hint: '#ffffff'
    },
    background: {
      paper: '#fff'
    },
    common: {
      white: "#FFF"
    },

  }

});

theme.overrides = {
   /* Button overrides not included to make this easier to read */

   MuiInputLabel: {

    root: {

      textTransform: 'uppercase',

      fontSize: '1.5rem',

    },

  },

  MuiInput: {

    root: {

      top: theme.spacing(2),

      border: `1px solid red`,

      outline: `1px solid transparent`,

      padding: theme.spacing(1),

      '&$focused': {

        border: `1px solid ${theme.palette.primary.main}`,

        outline: `1px solid ${theme.palette.primary.main}`,

      },

    },

    // we don't need `focused: {}` with overrides

  },

}
const Etapas: React.FC = () => {


  const _contexto = useContext<iEtapa>(EtapaContext);

  const [_ficha, setFicha] = useState<Ficha>(_contexto.value);

  const atualizar = useCallback(() => {
    _ficha.Etapa = 1;
    const teste = new Ficha();
    teste.Etapa = 1;
    setFicha(teste);
    console.log('atualizou')
  }, []);

  const getEtapa = function (index: number) {
    if (index == 0)
      return <Etapa01></Etapa01>;
    if (index == 1)
      return <Etapa02></Etapa02>;

  };

  return (
    <MuiThemeProvider theme={theme}>
      <EtapaContext.Provider value={{ value: _ficha, Atualizar: atualizar }}>
        <Container id="etapas" className="h-full">
          <EtapaContext.Consumer>
            {(context) => getEtapa(context.value.Etapa)}
          </EtapaContext.Consumer>
        </Container>


      </EtapaContext.Provider>
    </MuiThemeProvider>
  );
}

export default Etapas;
