import { createGlobalStyle } from 'styled-components'

export default createGlobalStyle`  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    margin: 0;
    padding: 0;
    color: ${props => props.theme.colors.text};
    font: 400 16px Roboto, sans-serif;
    min-height:100%;
  }
  #__next {
    min-height: 100%;
    display: flex;
  }


`
