import axios from 'axios';


export  class  CepService  {


  static  async buscarEndereco(cep: string) {
    axios.defaults.baseURL = 'https://viacep.com.br/ws' ;
    const request = axios.get('/'+cep+'/json');

    return (await request).data;
  }


}
