import styled from 'styled-components';

export const Container = styled.div`  * {
  .etapa{
    width:60%;
    .imagem{
      opacity: 0.3;
      width: 488px;
height: 386px;

    }
  }
  .sub-titulo{
    font-family: 'Roboto';
    font-style: normal;
    font-weight: bold;
    font-size: 4rem;
    line-height: 56px;
    color: #FFFFFF;
    text-align:center;
  }
  .mensagem{
    font-family: 'Roboto';
    font-style: normal;
    font-weight: bold;
    font-size: 32px;
    line-height: 40px;
    text-align: center;
    color: #FFFFFF;
    margin-top:2rem;
    text-align:center;

  }
}


`;
