import { createGlobalStyle } from 'styled-components'

export default createGlobalStyle`  * {
    margin: 0;
    padding: 0;
  }
  html,
body {
   margin: 0;
    color: ${props => props.theme.colors.text};
    font: 400 16px Roboto, sans-serif;
    min-height:100%;
    min-width:100%;
  }
  #__next {
    min-height:100%;
    position:absolute;
    min-width:100%;
  }

`
