import React, { useCallback, useContext } from 'react';
import Button from '@material-ui/core/Button';
import Input from '@material-ui/core/Input';
import InputAdornment from '@material-ui/core/InputAdornment';
import EmailIcon from '@material-ui/icons/Email';
import Send from '@material-ui/icons/Send';
import { useForm } from "react-hook-form";

import { Container } from './style';

import img_etapa_01 from '../../../../assets/solicitacaoCredito/etapa1.png'
import EtapaContext, { iEtapa } from '../etapaContext';
import { Ficha } from '../../../../Entities/Ficha';
import { TextField } from '@material-ui/core';



const Etapa01: React.FC = () => {

  const _contexto = useContext<iEtapa>(EtapaContext);

  const { register, handleSubmit, watch, errors } = useForm<Ficha>();
  const onSubmit = (data: Ficha) => {
    _contexto.value = Object.assign(_contexto.value, data);
    _contexto.value.Etapa = 2;
    _contexto.Atualizar(_contexto.value);
  };




  return (

    <Container className="h-full">
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="md:flex h-full ">
          <div className="content-end md:flex-shrink-0 md:w-2/4">
            <img className="items-end etapa w-full" src={img_etapa_01} />
          </div>
          <div className="m-6 h-full">
            <div className="bem-vindo antialiased">Bem-vindx à TMB</div>
            <div className="label-email antialiased">Informe seu e-mail</div>
            <div className="email">


              <TextField className="w-full "
                color="primary"
                inputRef={register({
                  required: "*Obrigatório.",
                  pattern: {
                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                    message: "*Endereço de e-mail inválido."
                  }
                })}
                name="Email"
                defaultValue={_contexto.value.Email}

                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <EmailIcon />

                    </InputAdornment>
                  ),
                }}
                helperText={errors.Email && errors.Email.message}
              />

            </div>
            <EtapaContext.Consumer>
              {(context) =>

                <div className="criarConta flex justify-end">
                  <Button
                    variant="contained"
                    className="botao"
                    type="submit"

                  >
                    Criar sua conta
              <Send className="ml-4" />
                  </Button>

                </div>
              }
            </EtapaContext.Consumer>
          </div>
        </div>
      </form>
    </Container>


  );
}

export default Etapa01;
