import EtapaContext, { iEtapa } from '../../etapaContext';
import { url } from 'inspector';
import { Produto } from '../../../../../Entities/Produto';


type Props = {
  produto: Produto,
  selecionado: boolean
}




export class ProdutoCard extends Component<Props> {
  render() {
    const img = '';
    const _contexto = useContext<iEtapa>(EtapaContext);

    return (

      <div className="w-full bg-white max-w-sm mx-auto rounded-md shadow-lg overflow-hidden ">
        <div className="flex items-end justify-end h-56 w-full bg-cover"      >
          <button className="p-2 rounded-full bg-blue-600 text-white mx-5 -mb-4 hover:bg-blue-500 focus:outline-none focus:bg-blue-500">
            {
              this.props.selecionado ?

                <svg className="h-5 w-5" fill="none" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" viewBox="0 0 24 24" stroke="currentColor"><path d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z"></path></svg>
                :
                <span>OK</span>
            }
          </button>
        </div>
        <div className="px-5 py-3">
          <h3 className="text-gray-700 uppercase">{props.produto?.Nome}</h3>
          <span className="text-gray-500 mt-2">R$ 3.700,00</span>
        </div>
      </div>

    );
  }
};
