import { createGlobalStyle } from 'styled-components'

export default createGlobalStyle`  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    background: linear-gradient(180deg, #003D6E 0%, #0F303E 99.95%, rgba(15, 48, 62, 0.0555158) 99.96%, rgba(15, 48, 62, 0.0458602) 99.97%, rgba(0, 43, 79, 0.0309089) 99.98%, rgba(0, 42, 79, 0.015625) 99.99%, rgba(0, 42, 78, 0) 100%);
    color: ${props => props.theme.colors.text};
    font: 400 16px Roboto, sans-serif;
  }
  #__next {
    height:100%;
    position:fixed;
  }

`
