// /pages/api/movies.js
import { PrismaClient } from '@prisma/client';

const prisma = new PrismaClient();

export default async function (req, res) {
  if (req.method === 'POST') {
    const { body } = req;
    const movie = await prisma.ficha.create({ data: JSON.parse(body) });
    res.json(movie);
  }
  if (req.method === 'GET') {
    res.statusCode = 200
  res.json({ name: 'John Doe' })
  }
}
