import React, { useContext } from 'react';
import Button from '@material-ui/core/Button';
import Input from '@material-ui/core/Input';
import InputAdornment from '@material-ui/core/InputAdornment';
import EmailIcon from '@material-ui/icons/Email';
import Send from '@material-ui/icons/Send';
import { useForm } from "react-hook-form";


import { Container } from './style';

import img_etapa from '../../../../assets/solicitacaoCredito/etapa3.png'
import EtapaContext, { iEtapa } from '../etapaContext';
import { Ficha } from '../../../../Entities/Ficha';


const Etapa03: React.FC = () => {

  const _contexto = useContext<iEtapa>(EtapaContext);

  const { register, handleSubmit, watch, errors } = useForm<Ficha>();
  const onSubmit = (data: Ficha) => {
    _contexto.value = Object.assign(_contexto.value, data);
    _contexto.value.Etapa = 5;
    _contexto.Atualizar(_contexto.value);
  };


  return (

    <Container className="h-full">
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="md:flex h-full ">
          <div className="md:flex place-items-center etapa">
            <img className="flex items-end imagem " src={img_etapa} />
          </div>
          <div className="m-6 h-full w-full content-center">
            <div className="sub-titulo antialiased">Legal {_contexto.value.Nome}, informe seu CPF</div>
            <div className="form flex items-center flex-wrap md: w-2/4">
              <div className="w-full justify-center ">
                <Input placeholder="Informe seu CPF" inputProps={{ 'aria-label': 'description' }}
                  inputRef={register({
                    required: "*Obrigatório.",
                    pattern: {
                      value: /^\d{3}\.\d{3}\.\d{3}\-\d{2}$/,
                      message: "*CPF inválido."
                    }
                  })}
                  name="CPF"
                  className="w-full"
                  defaultValue={_contexto.value.CPF}
                />
                {errors.CPF && errors.CPF.message}
              </div>
            </div>
            <div>

            </div>
            <div className="avancar_etapa flex justify-end sm:m-2 md:m-10">
              <Button
                variant="contained"
                className="botao"
                type="submit"
              >
                Avançar
              <Send className="ml-4" />
              </Button>
            </div>
          </div>
        </div>
      </form>
    </Container>


  );
}

export default Etapa03
  ;
