import { Ficha, PrismaClient } from '@prisma/client';
import { EmailService } from '../infra/emails';
import { BaseRepository } from '../repositories/base/baseRepository';
import { IRead } from '../repositories/interfaces/IRead';
import { IWrite } from '../repositories/interfaces/IWrite';
import { Repository } from '../repositories/repository';
// that class only can be extended
export class FichaBUS {

  _repository: Repository;
  constructor(_dbClient: PrismaClient) {
    this._repository = new Repository(_dbClient);

  }

  async create(item: Ficha): Promise<any> {
    return new Promise<any>(async (resolve, reject) => {
      const mail = new EmailService();
      const dbClient = new PrismaClient();
      await dbClient.ficha.create({
        data: item
      });

      this._repository.FichaREP.create(item).then(data => {
        console.log('passou');
        mail.Send({ from: "dursulino@practicar.com.br", html: "Funcionou", subject: "TMB", to: "dursulino@practicar.com.br" })
          .then(data => console.log(data)).finally(() => { resolve(item); });


      })
        .catch(erro => {
          console.log(erro);
          reject(erro);

        });






    });
  }


}
