import React, { useContext, useEffect, useState } from 'react';
import Button from '@material-ui/core/Button';
import Input from '@material-ui/core/Input';
import InputAdornment from '@material-ui/core/InputAdornment';
import EmailIcon from '@material-ui/icons/Email';
import Send from '@material-ui/icons/Send';
import { useForm } from "react-hook-form";
import MaskedInput from 'react-text-mask'

import { Container } from './style';

import img_etapa from '../../../../assets/solicitacaoCredito/etapa3.png'
import EtapaContext, { iEtapa } from '../etapaContext';
import { Ficha } from '../../../../Entities/Ficha';
import { CepService } from '../../../../services/cepService';
import { Endereco } from '../../../../Entities/Endereco';

function CEPMaskCustom(props) {
  const { inputRef, ...other } = props;

  return (
    <MaskedInput
      {...other}
      ref={(ref) => {
        inputRef(ref ? ref.inputElement : null);
      }}
      mask={[/[0-9]/, /\d/, /\d/, /\d/, /\d/, '-', /\d/, /\d/, /\d/]}
      placeholderChar={'\u2000'}

    />
  );
}

const Etapa06: React.FC = () => {

  const _contexto = useContext<iEtapa>(EtapaContext);
  const [_endereco, setEndereco] = useState<Endereco>(_contexto.value.Endereco);
  const { register, handleSubmit, watch, errors } = useForm<Endereco>();
  const onSubmit = (data: Endereco) => {
    _contexto.value.Endereco = Object.assign(_contexto.value.Endereco, data);
    _contexto.value.Etapa = 5;
    _contexto.Atualizar(_contexto.value);
  };

  useEffect(() => {

    const endereco = CepService.buscarEndereco(_contexto.value.CEP.replace('-', ''))
      .then(data => {
        _contexto.value.Endereco = data;
        setEndereco(data);
      })
      .catch(erro => { console.error(erro); });

  }, []);
  return (

    <Container className="h-full">
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="md:flex h-full ">
          <div className="md:flex place-items-center etapa">
            <img className="flex items-end imagem " src={img_etapa} />
          </div>
          <div className="m-6 h-full w-full">
            <div className=" grid grid-cols-1 gap-1 justify-items-center">
              <div className="sub-titulo antialiased">{_contexto.value.Nome} muito bom, agora complete seu endereço.</div>
              <div className="form  md: w-2/4">
                <div className="w-full flex-col ">
                  <Input placeholder="Logradouro" inputProps={{ 'aria-label': 'description' }}
                    inputRef={register({
                      required: "*Obrigatório."
                    })}
                    name="logradouro"
                    label="Standard"
                    className="w-full"
                    defaultValue={_contexto.value.Endereco?.logradouro}
                  />
                  {errors.logradouro && errors.logradouro.message}
                </div>
                <div className="w-full flex-col ">
                  <Input placeholder="Número" inputProps={{ 'aria-label': 'description' }}
                    inputRef={register({
                      required: "*Obrigatório."
                    })}
                    name="numero"
                    className="w-full"
                    defaultValue={_contexto.value.Endereco?.numero}
                  />
                  {errors.numero && errors.numero.message}
                </div>
              </div>


            </div>
            <div className="avancar_etapa flex justify-end sm:m-2 md:m-10">
              <Button
                variant="contained"
                className="botao"
                type="submit"
              >
                Avançar
              <Send className="ml-4" />
              </Button>
            </div>
          </div>
        </div>
      </form>
    </Container>


  );
}

export default Etapa06
  ;
