import styled from 'styled-components';

export const Container = styled.div`  * {
  .etapa{
    width:60%;
    .imagem{
      opacity: 0.3;
      width: 100%;

    }
  }
  .sub-titulo{
    font-family: 'Roboto';
    font-style: normal;
    font-weight: bold;
    font-size: 48px;
    line-height: 56px;
    color: #FFFFFF;
  }
  .mensagem{
    font-family: 'Roboto';
    font-style: normal;
    font-weight: bold;
    font-size: 36px;
    line-height: 42px;
    text-align: center;
    color: #FFFFFF;
  }
}


`;
