import React, { useContext, useEffect, useState } from 'react';
import Button from '@material-ui/core/Button';
import Input from '@material-ui/core/Input';
import InputAdornment from '@material-ui/core/InputAdornment';
import EmailIcon from '@material-ui/icons/Email';
import Send from '@material-ui/icons/Send';
import { useForm, Controller } from "react-hook-form";
import MaskedInput from 'react-text-mask'

import { Container } from './style';

import img_etapa from '../../../../assets/solicitacaoCredito/etapa3.png'
import EtapaContext, { iEtapa } from '../etapaContext';
import { Ficha } from '../../../../Entities/Ficha';
import { CepService } from '../../../../services/cepService';
import { Endereco } from '../../../../Entities/Endereco';
import TextField from '@material-ui/core/TextField/TextField';
import MaterialUIInput from "@material-ui/core/Input";


const Etapa07: React.FC = () => {

  const _contexto = useContext<iEtapa>(EtapaContext);
  const { setValue, register, handleSubmit, errors ,reset } = useForm<Ficha>();

  const onSubmit = (data: Endereco) => {
    _contexto.value.Endereco = Object.assign(_contexto.value.Endereco, data);
    _contexto.value.Etapa = 8;
    _contexto.Atualizar(_contexto.value);
  };

  useEffect(() => {

  }, [_contexto, reset ]);


  return (

    <Container className="h-full">
      <form onSubmit={handleSubmit(onSubmit)}  noValidate
      autoComplete="off">
        <div className="md:flex h-full ">
          <div className="md:flex place-items-center etapa">
            <img className="flex items-end imagem " src={img_etapa} />
          </div>
          <div className="m-6 h-full w-full">
            <div className=" grid grid-cols-1 gap-1 justify-items-center">
              <div className="sub-titulo antialiased">{_contexto.value.Nome}, agora informe seu número de celular.</div>
              <div className="form  md: w-3/4">
                <div className="w-full flex  flex-row">
                  <div className="flex pr-6 w-8/12" >

                    <TextField placeholder="(DD) 99999-9999"

                      inputRef={register({
                        required: "Obrigatório."
                      })}

                      name="Celular"
                      label="Celular"
                      className="w-full"
                      defaultValue={_contexto?.value?.Celular}
                      helperText={errors.Celular && errors.Celular.message}
                    />

                  </div>
                </div>

              </div>


            </div>
            <div className="avancar_etapa flex justify-end sm:m-2 md:m-10">
              <Button
                variant="contained"
                className="botao"
                type="submit"
              >
                Avançar
              <Send className="ml-4" />
              </Button>
            </div>
          </div>
        </div>
      </form>
    </Container>


  );
}

export default Etapa07 ;
