const prisma = new PrismaClient();

export default async function (req, res) {

  const fichaBUS =  new FichaBUS();
  if (req.method === 'POST') {

    const { body } = req;

     const value =Object.assign({celular: 'null', cep: 'null', cpf: 'null',email: 'null', endereco: 'null',etapa:'null', nome: 'null', token:'null'},JSON.parse(body));

 console.log(value);
     const ficha = await fichaBUS.create(value);
    res.json(ficha);
