import { createGlobalStyle } from 'styled-components'

export default createGlobalStyle`  * {
    margin: 0;
    padding: 0;

  }

  html, body {
    min-height: 100% !important;
    height: 100%;
    color: ${props => props.theme.colors.text};
    font: 400 16px Roboto, sans-serif;
    overflow:auto;
  }
  #__next {
    min-height:100%;
    width:100%;
    overflow:auto;
    display: block;
  position:absolute;
  }

`
