import styled from 'styled-components';

export const Container = styled.div`  * {



  .etapa{
    opacity: 0.3;
    mix-blend-mode: soft-light;
  }
  .bem-vindo{
    font-style: normal;
    font-weight: bold;
    font-size: 72px;
    line-height: 84px;
    color: #FFFFFF;
  }
  .label-email{
    font-style: normal;
    font-weight: normal;
    font-size: 42px;
    line-height: 75px;
    /* identical to box height */
    color: #FFFFFF;
  }
  .email{
    margin-top: 16px;
  }
  .criarConta{
    margin-top: 16px;

    .botao{
      background-color: #E92A2A;
      border-color: #E92A2A;
      color: #ffffff;


    }
    .MuiInput-underline:after{
        border-bottom-color: red;
      }
  }
}`;
