import { createGlobalStyle } from 'styled-components'

export default createGlobalStyle`  * {
    margin: 0;
    padding: 0;

  }

  body {
    min-height:100vh;
    color: ${props => props.theme.colors.text};
    font: 400 16px Roboto, sans-serif;
  }
  #__next {
    height:100%;
    width:100%;
  }

`
