import styled from 'styled-components';

export const Container = styled.div`  * {
  .etapa{
    width:60%;
    .imagem{
      opacity: 0.3;
      width: 488px;
height: 386px;

    }
  }
  .sub-titulo{
    font-family: 'Roboto';
    font-style: normal;
    font-weight: bold;
    color: #FFFFFF;
    text-align:center;

  }
  .form{

    margin-top:2rem;
  }
  .avancar_etapa{

    .botao{
      background-color: #E92A2A;
      border-color: #E92A2A;
      color: #ffffff;

    }
  }
}


`;
