import EmailIcon from '@material-ui/icons/Email';
import Send from '@material-ui/icons/Send';


import { Container } from './style';

import img_email from '../../../../assets/solicitacaoCredito/email.png'
import { Ficha } from '../../../../Entities/Ficha';
import EtapaContext, { iEtapa } from '../etapaContext';


const Etapa02: React.FC = () => {
  const _contexto = useContext<iEtapa>(EtapaContext);

  const avancar_etapa = function () {
    _contexto.Atualizar();

  };

  return (


    <Container className="h-full">

      <div className="md:flex h-full ">
        <div className="md:flex place-items-center etapa">
          <img className="flex items-end imagem " src={img_email} />
        </div>
        <div className="m-6 h-full w-full content-center" onClick={()=> avancar_etapa()} >
          <div className="sub-titulo antialiased">Acabei de te enviar um link por e-mail!</div>
          <div className="mensagem subpixel-antialiased">Dá uma olhadinha lá, clique no link e vamos juntos finalizar o seu cadastro!</div>

        </div>
      </div>
    </Container>


  );
}

export default Etapa02;
