import React, { useCallback, useContext } from 'react';
import Button from '@material-ui/core/Button';
import Input from '@material-ui/core/Input';
import InputAdornment from '@material-ui/core/InputAdornment';
import EmailIcon from '@material-ui/icons/Email';
import Send from '@material-ui/icons/Send';


import { Container } from './style';

import img_etapa_01 from '../../../../assets/solicitacaoCredito/etapa1.png'
import EtapaContext from '../etapaContext';
import { Ficha } from '../../../../Entities/Ficha';
import { withStyles, InputBase } from '@material-ui/core';


const CssTextField = withStyles({
  root: {
    '& label.Mui-focused': {
      color: 'green',
    },
    '& .MuiInput-underline:after': {
      borderBottomColor: 'green',
    },
    '& .MuiOutlinedInput-root': {
      '& fieldset': {
        borderColor: 'red',
      },
      '&:hover fieldset': {
        borderColor: 'yellow',
      },
      '&.Mui-focused fieldset': {
        borderColor: 'green',
      },
    },
  },
})(InputBase);

const Etapa01: React.FC = () => {


  const EnviarEmail = useCallback((value) => {
    console.log(value)
  }, []);

  return (

    <Container className="h-full">

      <div className="md:flex h-full ">
        <div className="content-end md:flex-shrink-0 md:w-2/4">
          <img className="items-end etapa w-full" src={img_etapa_01} />
        </div>
        <div className="m-6 h-full">
          <div className="bem-vindo antialiased">Bem-vindx Ã  TMB</div>
          <div className="label-email antialiased">Informe seu e-mail</div>
          <div className="email">
            <CssTextField id="txtEmail" className="w-full "
        color="secondary"
              startAdornment={
                <InputAdornment position="start">
                  <EmailIcon />
                </InputAdornment>
              }
            />

          </div>
          <EtapaContext.Consumer>
            {(context) =>

              <div className="criarConta flex justify-end">
                <Button
                  variant="contained"
                  className="botao"
                  onClick={context.Atualizar}
                >
                  Criar sua conta
              <Send className="ml-4" />
                </Button>

              </div>
            }
          </EtapaContext.Consumer>
        </div>
      </div>
    </Container>


  );
}

export default Etapa01;
